INSERT INTO `wp_options` VALUES(87, 0, 'widget_text', 'a:14:{i:2;a:0:{}i:5;a:3:{s:5:"title";s:0:"";s:4:"text";s:297:"<a href="http://www.amazon.com/Reasonable-People-Adoption-Neurological-Difference/dp/1590511298/ref=pd_bbs_sr_1?ie=UTF8&s=books&qid=1205087348&sr=8-1"><img src="http://grantcuster.com/savarese/wp-content/themes/twentyten/images/amazonlogo.png" title="Purchase Reasonable People at Amazon.com"></a>";s:6:"filter";b:0;}i:6;a:3:{s:5:"title";s:0:"";s:4:"text";s:249:"<a href="http://www.hws.edu/academics/senecareview/index.aspx" title="Visit the Seneca Review website for more information or to purchase a copy"><img src=http://grantcuster.com/savarese/wp-content/themes/twentyten/images/seneca-review-logo.png></a>";s:6:"filter";b:0;}i:7;a:3:{s:5:"title";s:12:"Contributors";s:4:"text";s:527:"<h4>Essays</h4>\nSusanne Antonnetta, Jane Bernstein, Barrie Jean Borich, Anne Finger, Jennifer Finney Boylan, Joy Castro, Mark Doty, Rebecca Epstein, Elizabeth Graver, Binnie Klein, Amitava Kumar, J. Harley McIlrath, Tito Mukhopadyhyay, Susan Neeley, Achim Nowak, Gregory Orr, Dawn Prince, Floyd Skloot, Laura C. Stevenson\n\n<h4>Poetry</h4>\nMelanie Almeder, George Barlow, Rafael Campo, John Lee Clark, Jim Ferris, Katie Ford, Ona Gritz, Sam Hamill, Ilya Kaminsky, Laurie Clements Lambeth, Thomas Lynch, F.D. Reeve, Adrienne Rich";s:6:"filter";b:1;}i:8;a:3:{s:5:"title";s:5:"Links";s:4:"text";s:545:"<a href="http://www.taaproject.com/">The Autism Acceptance Project</a>\n<a href="http://www.kristinachew.com/">Autismland: Kristina Chew''s Blog</a>\n<a href="http://www.autistics.org/">Autistics.org</a>\n<a href="http://www.tash.org/breaking-the-barriers/>Breaking the Barriers</a>\n<a href="http://www.michaelberube.com/">Michael Bérubé</a>\n<a href="http://soe.syr.edu/centers_institutes/institute_communication_inclusion/default.aspx">Institute on Communication and Facilitation</a>\n<a href="http://www.neurodiversity.com">Neurodiversity.com</a>";s:6:"filter";b:0;}i:10;a:3:{s:5:"title";s:0:"";s:4:"text";s:218:"<a href="http://www.amazon.com/Papa-PhD-Essays-Fatherhood-Academy/dp/0813548799"><img src="http://grantcuster.com/savarese/wp-content/themes/twentyten/images/amazonlogo.png" title="Purchase Papa PhD at Amazon.com"></a>";s:6:"filter";b:0;}i:11;a:3:{s:5:"title";s:0:"";s:4:"text";s:160:"<a href="http://rutgerspress.rutgers.edu/acatalog/Papa_Phd.html"><img src="http://grantcuster.com/savarese/wp-content/themes/twentyten/images/rutgers.png" ></a>";s:6:"filter";b:0;}i:12;a:3:{s:5:"title";s:12:"Contributors";s:4:"text";s:525:"Amitava Kumar, Lennard J. Davis, David G. Campbell, F. D. Reeve, Jerald Walker, John W. Wells, Joseph Gelfer, David Haven Blake, Ira L. Strauber,  J. Aaron Simmons, Gregory Orfalea, Robert Mayer, Ralph James Savarese, Mark Montgomery, Robert Gray, John Bryant, Mark Osteen, Kevin G. Barnhurst, Andrew Hazucha, William Marsiglio, Eric H. du Plessis, Stanford W. Carpenter, Jason Thompson, Mike Augspurger, Ting Man Tsao, Jeffrey B. Leak, John Domini, Charles Bane, Alex Vernon, Colin Irvine, Chris Gabbard, Gary H. McCullough\n";s:6:"filter";b:0;}i:13;a:3:{s:5:"title";s:0:"";s:4:"text";s:141:"<a href="http://www.dsq-sds.org/issue/view/43"><img src="http://grantcuster.com/savarese/wp-content/themes/twentyten/images/dsqlink.png"></a>";s:6:"filter";b:0;}i:14;a:3:{s:5:"title";s:0:"";s:4:"text";s:118:"<h3 class="widget-title"><a href="http://ralphsavarese.com/category/reasonable-people/praise/">Advance Praise</a></h3>";s:6:"filter";b:0;}i:15;a:3:{s:5:"title";s:0:"";s:4:"text";s:130:"<h3 class="widget-title"><a href="http://ralphsavarese.com/category/reasonable-people/readers-write-us/">Readers Write Us</a></h3>";s:6:"filter";b:0;}i:18;a:3:{s:5:"title";s:12:"Contributors";s:4:"text";s:745:"Amanda Baggs, Kristina Chew, Anne M. Donnellan, Paula C. Durbin-Westby, Chris Gabbard, Meredyth Goldberg Edelson, Katie Ellis, David A. Hill, Joseph F. Kras, Bruce Mills, Tito Rajarshi Mukhopadhyay, Stuart Murray, Ari Ne’eman, Valerie Paradiz, Melissa Park, Nick Pentzel, Dawn Prince, Scott Michael Robertson, Bill Rocque, Irene Rose, DJ Savarese, Emily Thornton Savarese, Ralph James Savarese, Jim Sinclair, Melanie Yergeau, Zosia Zaks\n<br /><br />\n<h4>Parent Roundtable</h4>\nPamela Block, Kristina Chew, Debra Cumberland, John Duffy, Susan Etlinger, Estee Klar, Mark Osteen, James Wilson\n<br /><br />\n<h4>Self-Advocate Roundtable</h4>\nJamie Burke, Sharisa Kochmeister, Sue Rubin, Phil Schwarz, Stephen Shore, Kassianne Sibley, Tracy Thresher";s:6:"filter";b:0;}i:19;a:3:{s:5:"title";s:15:"Sample Writings";s:4:"text";s:0:"";s:6:"filter";b:0;}s:12:"_multiwidget";i:1;}', 'yes');
