INSERT INTO `wp_posts` VALUES(112, 1, '2010-08-22 13:15:07', '2010-08-22 20:15:07', '“Articulate and Passionate, Savarese''s intricately crafted memoir of his son''s early years challenges us all to embrace diversity, to triumph over adversity, and to become not just reasonable people--but a reasonable society."', 'Morton Ann Gernsbacher, Ph.D.', '', 'publish', 'open', 'open', '', 'morton-ann-gernsbacher-ph-d', '', '', '2010-09-06 15:28:02', '2010-09-06 22:28:02', '', 0, 'http://grantcuster.com/savarese/?p=112', 0, 'post', '', 0);
INSERT INTO `wp_posts` VALUES(113, 1, '2010-08-22 13:14:51', '2010-08-22 20:14:51', '“Articulate and Passionate, Savarese''s intricately crafted memoir of his son''s early years challenges us all to embrace diversity, to triumph over adversity, and to become not just reasonable people--but a reasonable society."', 'Morton Ann Gernsbacher, Ph.D.', '', 'inherit', 'open', 'open', '', '112-revision', '', '', '2010-08-22 13:14:51', '2010-08-22 20:14:51', '', 112, 'http://grantcuster.com/savarese/uncategorized/112-revision/', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES(114, 1, '2010-08-22 13:16:39', '2010-08-22 20:16:39', '“<em>Reasonable People</em> is a work of righteous fury, a keenly written memoir and a sustained polemic about autism. It is a literary investigation that swarms with life—I couldn’t put it down—and to read it is to be changed by its deeply felt suffering, its stubborn joy.”', 'Edward Hirsch', '', 'publish', 'open', 'open', '', 'edward-hirsch', '', '', '2010-09-06 15:27:53', '2010-09-06 22:27:53', '', 0, 'http://grantcuster.com/savarese/?p=114', 0, 'post', '', 0);
INSERT INTO `wp_posts` VALUES(115, 1, '2010-08-22 13:16:14', '2010-08-22 20:16:14', '“<em>Reasonable People</em> is a work of righteous fury, a keenly written memoir and a sustained polemic about autism. It is a literary investigation that swarms with life—I couldn’t put it down—and to read it is to be changed by its deeply felt suffering, its stubborn joy.”', 'Edward Hirsch', '', 'inherit', 'open', 'open', '', '114-revision', '', '', '2010-08-22 13:16:14', '2010-08-22 20:16:14', '', 114, 'http://grantcuster.com/savarese/uncategorized/114-revision/', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES(116, 1, '2010-08-21 13:25:35', '2010-08-21 20:25:35', '“<em>Reasonable People</em> is at once an extraordinary story of one boy’s complex emergence into language and connection, and an urgent manifesto for every human being’s right to self-determination and love. ‘I try to get free you try’ writes DJ Savarese. With poetry, humor, and a fierce intelligence as deep as it is wide, Ralph Savarese—together with his son—shows us how.”', 'Elizabeth Graver', '', 'publish', 'open', 'open', '', 'elizabeth-graver', '', '', '2010-09-06 15:28:50', '2010-09-06 22:28:50', '', 0, 'http://grantcuster.com/savarese/?p=116', 0, 'post', '', 0);
INSERT INTO `wp_posts` VALUES(117, 1, '2010-08-22 13:18:02', '2010-08-22 20:18:02', '“<em>Reasonable People</em> is at once an extraordinary story of one boy’s complex emergence into language and connection, and an urgent manifesto for every human being’s right to self-determination and love. ‘I try to get free you try’ writes DJ Savarese. With poetry, humor, and a fierce intelligence as deep as it is wide, Ralph Savarese—together with his son—shows us how.”', 'Elizabeth Graver', '', 'inherit', 'open', 'open', '', '116-revision', '', '', '2010-08-22 13:18:02', '2010-08-22 20:18:02', '', 116, 'http://grantcuster.com/savarese/uncategorized/116-revision/', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES(118, 1, '2010-08-20 13:27:26', '2010-08-20 20:27:26', '“Through the methodical, careful teaching of the meaning of language, DJ, a non-verbal person with autism, was able to successfully communicate his shocking family history. This book will be exceedingly valuable for all parents and teachers who are working with non-verbal individuals.”', 'Temple Grandin', '', 'publish', 'open', 'open', '', 'temple-grandin', '', '', '2010-09-06 15:29:04', '2010-09-06 22:29:04', '', 0, 'http://grantcuster.com/savarese/?p=118', 0, 'post', '', 0);
INSERT INTO `wp_posts` VALUES(435, 1, '2010-08-29 14:23:07', '2010-08-29 21:23:07', '<em>Editors</em> Thinking Stiffs: An Introduction\r\n\r\n<h4><span>Part One</span><br />Fathers in Theory Fathers in Praxis: Merging Work and Parenting</h4>\r\n\r\n<em>Amitava Kumar</em> Disney Dad\r\n\r\n<em>Lennard J. Davis</em> Gaining a Daughter: A Father’s Transgendered Tale\r\n\r\n<em>David G. Campbell</em> Gifts from the Sea\r\n\r\n<em>F. D. Reeve</em> The Luck of the Irish\r\n\r\n<em>Jerald Walker</em> Shifting the Tectonic Plates of Academia\r\n\r\n<em>John W. Wells</em> Hair-Raising Experiences\r\n\r\n<em>Joseph Gelfer</em> A River Runs through It: Queer Theory and Fatherhood\r\n\r\n<em>David Haven Blake</em> On Writing and Rearing\r\n\r\n<em>Ira L. Strauber</em> Doing Things with Words\r\n\r\n<em>J. Aaron Simmons</em> On Fecundity Fidelity and Expectation: Reflections on Philosophy and Fatherhood\r\n\r\n<em>Gregory Orfalea</em> Sheathing the Sword\r\n\r\n<h4><span>Part Two</span><br />Family Made: The Difference of Alternative or Delayed Fatherhood</h4>\r\n\r\n<em>Robert Mayer</em> Weighed but Found Wanting: Ten Years of Being Measured and Divided\r\n\r\n<em>Ralph James Savarese</em> Vespers Matins Lauds: The Life of a Liberal Arts College Professor\r\n\r\n<em>Mark Montgomery</em> How White Was My Prairie\r\n\r\n<em>Robert Gray</em> Meniscus\r\n\r\n<em>John Bryant</em> Once Was Lost\r\n\r\n<em>Mark Osteen</em> Shared Attention: Hearing Cameron’s Voice\r\n\r\n<em>Kevin G. Barnhurst</em> Accidental Academic Deliberate Dad\r\n\r\n<em>Andrew Hazucha</em> Late Fatherhood among the Baptists\r\n\r\n<em>William Marsiglio</em> Being a Dad Studying Fathers: Personal Reflections\r\n\r\n<em>Eric H. du Plessis</em> Single Dad in Academia: Fatherhood and the Redemption of Scholarship\r\n\r\n<em>Stanford W. Carpenter</em> Superheroes\r\n\r\n<h4><span>Part Three</span><br />Forging New Fatherhoods: Ambitions Altered and Transformed</h4>\r\n\r\n<em>Jason Thompson</em> Maybe It Is Just Math: Fatherhood and Disease in Academia\r\n\r\n<em>Mike Augspurger</em> Dreaming of Direction: Reconciling Fatherhood and Ambition\r\n\r\n<em>Ting Man Tsao</em> Making a Home for Family and Scholarship\r\n\r\n<em>Jeffrey B. Leak</em> Change Is Here but We Need to Talk about It: Reflections on Black Fatherhood in the Academy\r\n\r\n<em>John Domini</em> Vocabularies and Their Subversion: A Reminiscence\r\n\r\n<em>Charles Bane</em> Balancing Diapers and a Doctorate: The Adventures of a Single Dad in Grad School\r\n\r\n<em>Alex Vernon</em> It’s a Chapter-Book Huh: Teaching Writing and Early Fatherhood\r\n\r\n<em>Colin Irvine</em> Pitcher This: An Academic Dad’s Award-Winning Attempt to Be in Two Places at Once\r\n\r\n<em>Chris Gabbard</em> Odd Quirks\r\n\r\n<em>Gary H. McCullough</em> The Precarious Private Life of Professor Father Fiction Chef and Other Possible Poignancies', 'Table of Contents', '', 'publish', 'closed', 'closed', '', 'table-of-contents', '', '', '2010-08-29 15:23:21', '2010-08-29 22:23:21', '', 56, 'http://grantcuster.com/savarese/', 0, 'page', '', 0);
