INSERT INTO `wp_posts` VALUES(126, 1, '2010-08-17 13:36:03', '2010-08-17 20:36:03', '“<em>Reasonable People</em> is the story of a homemade disability rights movement, one that defies many contemporary institutional and social service expectations about autistic children. This is also a book with a historian’s care for facts and a poet’s concern with emotional candor. It belongs on the shelf with the best work in disability history and memoir.”', 'Stephen Kuusisto', '', 'publish', 'open', 'open', '', 'stephen-kuusisto', '', '', '2010-09-06 15:29:34', '2010-09-06 22:29:34', '', 0, 'http://grantcuster.com/savarese/?p=126', 0, 'post', '', 0);
