INSERT INTO `wp_posts` VALUES(134, 1, '2010-08-14 13:41:58', '2010-08-14 20:41:58', '“I loved the book, although it really was painful to read about DJ’s life in foster care. The awesome dedication of Emily and Ralph transformed a hurt, isolated, non-verbal, autistic boy into a wonderful advocate for facilitated communication and inclusion in education and the community.”', 'Sue Rubin', '', 'publish', 'open', 'open', '', 'sue-rubin', '', '', '2010-09-06 15:30:17', '2010-09-06 22:30:17', '', 0, 'http://grantcuster.com/savarese/?p=134', 0, 'post', '', 0);
INSERT INTO `wp_posts` VALUES(135, 1, '2010-08-22 13:41:04', '2010-08-22 20:41:04', '', 'Sue Rubin', '', 'inherit', 'open', 'open', '', '134-revision', '', '', '2010-08-22 13:41:04', '2010-08-22 20:41:04', '', 134, 'http://grantcuster.com/savarese/uncategorized/134-revision/', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES(136, 1, '2010-08-14 13:43:15', '2010-08-14 20:43:15', '“In this comprehensive, charming book about the author’s adopted autistic child, DJ, Savarese covers a wide swath of literary, socio-cultural, educational, and psychological influences on the rearing of a challenging, often unruly, mute young child. The reader is intriguingly led by the hand through the trials and triumphs of parents finding their way to engage their child, and seeking the family and educational services appropriate to his condition. We learn about DJ’s horrific past with his biological parents and foster caretakers. Gratefully we read of how a youth, together with his dedicated, nurturing parents, learns to communicate with his adoptive family in any way he can, stemming the often inexorable tide of anguish and despair that children like DJ experience. An inspiring, informative read.”', 'Karen Zelan, Ph.D.', '', 'publish', 'open', 'open', '', 'karen-zelan-ph-d', '', '', '2010-09-06 15:30:09', '2010-09-06 22:30:09', '', 0, 'http://grantcuster.com/savarese/?p=136', 0, 'post', '', 0);
