INSERT INTO `wp_posts` VALUES(151, 1, '2002-12-22 15:34:02', '2002-12-22 22:34:02', 'Prairie dogs live in burrows,\r\nRace out of their holes.\r\nA lot of people like to catch them\r\nIn cages,\r\nRolling the little ones in paint,\r\nIdentifying them in the wild.\r\nEverybody is trying to catch one.\r\n\r\nDogs fight in spring time.\r\nOther females in need of meat\r\nGet their babies from others'' nests.\r\nSome get eaten.', 'Prairie Dogs', '', 'publish', 'open', 'open', '', 'prairie-dogs', '', '', '2010-09-20 13:14:29', '2010-09-20 20:14:29', '', 0, 'http://grantcuster.com/savarese/?p=151', 0, 'post', '', 0);
