INSERT INTO `wp_posts` VALUES(241, 1, '2010-08-23 13:58:39', '2010-08-23 20:58:39', 'I tore through your stunning book in 3 nights, was blown away by it. On every level. Linguistic/intellectual fascination with trauma material finally trumped by sheer monumentality of your and Emily''s undertaking, DJ''s ability, and inspiration. I hope your book goes everywhere, and that you''re doing well in what must be a marathon.', 'C.K.', '', 'publish', 'open', 'open', '', 'c-k', '', '', '2010-09-06 15:14:00', '2010-09-06 22:14:00', '', 0, 'http://grantcuster.com/savarese/?p=241', 0, 'post', '', 0);
