INSERT INTO `wp_posts` VALUES(247, 1, '2010-08-23 14:00:38', '2010-08-23 21:00:38', 'Just to say that I have here a cutting from the LA times with your marvelous essay and do not quite know what to do with it. But before I file it in the waste paper basket I wanted to thank you.\r\nThanks.', 'M.C.', '', 'publish', 'open', 'open', '', 'm-c', '', '', '2010-09-06 15:13:36', '2010-09-06 22:13:36', '', 0, 'http://grantcuster.com/savarese/?p=247', 0, 'post', '', 0);
