INSERT INTO `wp_posts` VALUES(265, 1, '2010-08-23 14:07:13', '2010-08-23 21:07:13', 'Thanks so much for sharing your family''s story in Reasonable People. It is an incredible testament of love, determination and hope and it had a great affect on me. You must be so proud of DJ and all he has accomplished.\r\n\r\nI wish you, Emily and DJ all the best and thank you again for sharing your family experiences.', 'D.L.', '', 'publish', 'open', 'open', '', 'd-l', '', '', '2010-09-06 14:14:08', '2010-09-06 21:14:08', '', 0, 'http://grantcuster.com/savarese/?p=265', 0, 'post', '', 0);
