INSERT INTO `wp_posts` VALUES(267, 1, '2010-08-23 14:07:48', '2010-08-23 21:07:48', 'I just finished reading your book. What a wonderful experience. You are a gifted and talented storyteller and above all a loving, kind and demonstrative father. I was so touched by your beautiful story and by the family that you have helped to create.', 'J.J.', '', 'publish', 'open', 'open', '', 'j-j', '', '', '2010-09-06 14:14:03', '2010-09-06 21:14:03', '', 0, 'http://grantcuster.com/savarese/?p=267', 0, 'post', '', 0);
