INSERT INTO `wp_posts` VALUES(300, 1, '2010-08-23 14:21:33', '2010-08-23 21:21:33', 'Thanks for your book, for your courage. I am a school psychologist in texas – awaiting your wife’s writings on how to include nonverbal children with autism in the regular school setting.', 'A.A.', '', 'publish', 'open', 'open', '', 'a-a', '', '', '2010-08-28 14:19:13', '2010-08-28 21:19:13', '', 0, 'http://grantcuster.com/savarese/?p=300', 0, 'post', '', 0);
