INSERT INTO `wp_posts` VALUES(312, 1, '2010-08-23 14:26:27', '2010-08-23 21:26:27', 'I read your wonderful book with great emotion and interest. I remember when you were in the process of adopting DJ when we met you in Vermont many years ago, and was amazed and excited to hear your voice on NPR this fall and realize it was you! I bought and read the book right away and found it extraordinarily moving and beautiful. DJ, you are a terrific writer and a very sensitive and intelligent person, and I hope to meet you some day.', 'K.O.', '', 'publish', 'open', 'open', '', 'k-o', '', '', '2010-08-28 14:18:23', '2010-08-28 21:18:23', '', 0, 'http://grantcuster.com/savarese/?p=312', 0, 'post', '', 0);
