INSERT INTO `wp_posts` VALUES(330, 1, '2010-08-23 14:32:25', '2010-08-23 21:32:25', 'I just finished your book. It is an awesome work of love. You and your wife have liberated a wise soul in your son DJ. Thank you for not giving up on him and for bringing his story to the world. I will recommend your book to my friends and family and also to the autism group (ASCEND) that I belong to.', 'Anon.', '', 'publish', 'open', 'open', '', 'anon', '', '', '2010-08-28 14:17:37', '2010-08-28 21:17:37', '', 0, 'http://grantcuster.com/savarese/?p=330', 0, 'post', '', 0);
