INSERT INTO `wp_posts` VALUES(447, 1, '2010-02-23 09:44:25', '2010-02-23 16:44:25', 'Listen to Emily and Ralph discuss the importance of the concept of neurodiversity on Iowa Public Radio’s “The Exchange.” Guests include two contributors to the special issue of DSQ: Jamie Burke, a student with autism at Syracuse University, and Scott Michael Robertson, a graduate student with Asperger syndrome at Penn State University and vice president of the Autistic Self-Advocacy Network (ASAN).', 'Emily and Ralph on the Exchange', '', 'publish', 'closed', 'closed', '', 'emily-and-ralph-on-the-exchange', '', '', '2010-09-20 12:49:49', '2010-09-20 19:49:49', '', 0, 'http://grantcuster.com/savarese/?p=447', 0, 'post', '', 0);
