INSERT INTO `wp_posts` VALUES(762, 1, '2007-05-24 19:53:11', '2007-05-25 02:53:11', '<em>Reasonable People: A Memoir of Autism & Adoption</em> is poet Ralph James Savarese''s tale of adopting an abused, non-speaking boy, then using love and patience to help his son grow into his full self.  A moving memoir, it calls for "living with conviction in a cynical time."', 'Body + Soul Magazine', '', 'publish', 'open', 'open', '', 'body-soul-magazine', '', '', '2010-09-27 19:53:48', '2010-09-28 02:53:48', '', 0, 'http://www.ralphsavarese.com/?p=762', 0, 'post', '', 0);
