INSERT INTO `wp_posts` VALUES(925, 1, '2012-05-03 17:26:23', '2012-05-04 00:26:23', 'From <em>The Seneca Review</em>, Fall 2011, Vol. 41/2.\n\nLong before he''d put his trust in antitrust litigation,\n    defending high-profile companies from the charge\nof monopolistic behavior, he''d dreamt of being on the court,\n    and there he was, though not exactly in robes,\noffering up his considered opinions: "Out!" "Out!" "Long!" "Long!"\n    My father was playing Justice Stevens\nin the annual Washington Golf and Country Club championships,\n    the 1%er version of a constitutional battle.\n\nRead more of "The Justice and My Father."', 'The Justice and My Father', '', 'inherit', 'open', 'open', '', '923-revision', '', '', '2012-05-03 17:26:23', '2012-05-04 00:26:23', '', 923, 'http://www.ralphsavarese.com/uncategorized/923-revision/', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES(926, 1, '2012-05-03 17:28:43', '2012-05-04 00:28:43', 'From <em>The Seneca Review</em>, Fall 2011, Vol. 41/2.\r\n\r\nLong before he''d put his trust in antitrust litigation,\r\n    defending high-profile companies from the charge\r\nof monopolistic behavior, he''d dreamt of being on the court,\r\n    and there he was, though not exactly in robes,\r\noffering up his considered opinions: "Out!" "Out!" "Long!" "Long!"\r\n    My father was playing Justice Stevens\r\nin the annual Washington Golf and Country Club championships,\r\n    the 1%er version of a constitutional battle.\r\n\r\nRead more of <a href="http://www.ralphsavarese.com/wp-content/uploads/2012/05/Savarese_The_Justice_and_My_Father.pdf">"The Justice and My Father" (PDF).</a>', 'The Justice and My Father', '', 'inherit', 'open', 'open', '', '923-revision-2', '', '', '2012-05-03 17:28:43', '2012-05-04 00:28:43', '', 923, 'http://www.ralphsavarese.com/uncategorized/923-revision-2/', 0, 'revision', '', 0);
