INSERT INTO `wp_posts` VALUES(1075, 1, '2013-08-23 07:09:12', '2013-08-23 14:09:12', 'He will join its neurohumanities research group for the academic year 2012-2013.', 'Ralph wins Humanities Writ Large Fellowship at Duke University’s Institute for Brain Science', '', 'inherit', 'open', 'open', '', '906-autosave', '', '', '2013-08-23 07:09:12', '2013-08-23 14:09:12', '', 906, 'http://www.ralphsavarese.com/uncategorized/906-autosave/', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES(1076, 1, '2013-08-23 07:13:59', '2013-08-23 14:13:59', '<a href="http://www.ralphsavarese.com/wp-content/uploads/2011/08/DJ-Graduation.jpg"><img class="alignnone size-large wp-image-889" title="DJ-Graduation" src="http://www.ralphsavarese.com/wp-content/uploads/2011/08/DJ-Graduation-1024x682.jpg" alt="" width="640" height="426" /></a>', 'DJ graduates from Grinnell High School with Highest Honors', '', 'inherit', 'open', 'open', '', '890-autosave', '', '', '2013-08-23 07:13:59', '2013-08-23 14:13:59', '', 890, 'http://www.ralphsavarese.com/uncategorized/890-autosave/', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES(1079, 1, '2013-08-03 12:58:31', '2013-08-03 19:58:31', '<strong>From <em>Research and Practice for Persons with Severe Disabilities </em>37.2 (2012). 100-110.</strong>\r\n\r\nEmily Thornton Savarese\r\nGrinnell, Iowa\r\n\r\nRalph James Savarese\r\nGrinnell College\r\n\r\nThis article underscores the importance of coupling a practical orientation to the many challenges of autism with a philosophical and political orientation that refuses merely to make room for disability in the classroom or to accommodate its specific needs. It uses a case study approach to illustrate the ways in which subject matter and narrative convention were problematic for a nonspeaking, autistic boy who had been abused in foster care. The article focuses on his elementary and middle school years to illustrate the way in which an understanding of disability studies must inform our reading pedagogy if marginalized children are to find a safe place in our classrooms and our communities.\r\n\r\nRead more of <a href="http://www.ralphsavarese.com/wp-content/uploads/2013/08/Literate-Lungs.pdf">Literate Lungs: One Autist’s Journey as a Reader (PDF)</a>', 'Literate Lungs: One Autist’s Journey as a Reader', '', 'publish', 'open', 'open', '', 'literate-lungs-one-autist%e2%80%99s-journey-as-a-reader', '', '', '2013-08-28 09:33:01', '2013-08-28 16:33:01', '', 0, 'http://www.ralphsavarese.com/?p=1079', 0, 'post', '', 0);
